(()=>{"use strict";const t=document.getElementById("root");new class{button=document.createElement("button");buttonText=document.createTextNode("collapse");buttonClasses=this.button.classList;constructor(){this.buttonClasses.add("collapsible"),this.button.appendChild(this.buttonText),this.content=document.createElement("div");const t=document.createTextNode("В рамках реализации большого портала вам необходимо сделать виджет collapsible-контейнеров, который может разворачиваться и сворачиваться.");this.content.classList.add("content"),this.content.appendChild(t)}init(){this.addPend(),this.onClick()}addPend(){document.body.appendChild(this.button),document.body.appendChild(this.content)}onClick(){this.button.addEventListener("click",(()=>{"block"===this.content.style.display?this.content.style.display="none":this.content.style.display="block"}))}}(t).init()})();